@mixin table-variant($state, $background) {
  --#{$prefix}table-color: var(--#{$prefix}emphasis-color);
  --#{$prefix}table-bg: #{$background};
  --#{$prefix}table-border-color: #{mix($background, $border-color-translucent, percentage($border-opacity))};
  --#{$prefix}table-striped-bg: #{mix($background, $white, percentage(.95 * $table-striped-bg-factor))};
  --#{$prefix}table-striped-color: #{color-contrast(mix($background, $white, percentage(.95 * $table-striped-bg-factor)))};
  --#{$prefix}table-active-bg: #{mix($background, $white, percentage(.85 * $table-active-bg-factor))};
  --#{$prefix}table-active-color: #{color-contrast(mix($background, $white, percentage(.85 * $table-active-bg-factor)))};
  --#{$prefix}table-hover-bg: #{mix($background, $white, percentage(.9 * $table-hover-bg-factor))};
  --#{$prefix}table-hover-color: #{color-contrast(mix($background, $white, percentage(.9 * $table-hover-bg-factor)))};

  color: var(--#{$prefix}table-color);
  border-color: var(--#{$prefix}table-border-color);
}

