describe('use jasmine.clock()', () => {
  it('should auto enter fakeAsync', () => {
    jasmine.clock().install();
    let called = false;
    setTimeout(() => {
      called = true;
    }, 100);
    jasmine.clock().tick(100);
    expect(called).toBe(true);
    jasmine.clock().uninstall();
  });

  it('should error if nested', () => {
    expect(() => {
      fakeAsync(() => {
        jasmine.clock().install();
      })();
    }).toThrowError(/cannot be nested/);
  });
});

